/** ÊùêÊñôÊï∞ÊçÆ */
export type MaterialData = {
    /** ÊùêÊñôid */ id: MaterialId;
    /** ÂêçÁß∞ */ name: String;
    /** Ê†áÁ≠æ */ tags: Array<MaterialTag>;
    /** Áà∂Á±ª */ parent: String | null;
    /** ÂèçÂ∫îÁªßÊâø */ inheritReactions: Boolean;
    /** ÂèØÁáÉÊÄß */ burnable: Boolean;
    /** ÂØÜÂ∫¶ */ density: Number;
    /** Á±ªÂûã */ type: MaterialType;
    /** Á°¨Â∫¶ */ durability: Number;
    /** Ë°ÄÈáè */ hp: Number;
    /** ÁáÉÁÉßË°ÄÈáè */ fireHp: Number;
    /** ÂØºÁîµÊÄß */ electricalConductivity: Boolean;
    /** ÁîüÊàêÁÉüÈõæ */ generatesSmoke: Boolean;
    /** Ê∂≤‰ΩìÈáçÂäõ */ liquidGravity: Number;
    /** ÊµÅÊ≤ôÁ±ªÂûã */ liquidSand: Boolean;
    /** Êë©Êì¶Âäõ */ solidFriction: Number;
    /** ÁÅ´ÁÑ∞Ê∏©Â∫¶ */ temperatureOfFire: Number;
    /** Ëá™ÁáÉÊ∏©Â∫¶ */ autoignitionTemperature: Number;
    /** ÂßãÁªàÁáÉÁÉß */ onFire: Boolean;
    /** ÈúÄË¶ÅÊ∞ßÊ∞î */ requiresOxygen: Boolean;
    /** È¢úËâ≤ */ color: String;
    /** ÊùêË¥® !`Â∑≤ÁßªÈô§` */ texture: String;
    /** ÊëÑÂÖ•ÊïàÊûú */ statusEffects_ingestion: String;
    /** Ê≤æÊüìÊïàÊûú */ statusEffects_stains: String;
    /** ÂÜªÁªìËΩ¨Âåñ */ coldFreezesToMaterial: MaterialData;
    /** Âä†ÁÉ≠ËΩ¨Âåñ */ warmthMeltsToMaterial: MaterialData;
    /** Á¢éË£ÇËΩ¨Âåñ */ solidBreakToType: MaterialData;
    /** Â≠òÂú®Êó∂Èó¥ */ lifetime: Number;
    /** Âπ≥Âè∞Á±ªÂûã */ platformType: Boolean;
    /** ÂêçÁß∞ÁøªËØëÈîÆ */ nameKey: String;
    /* ÂõæÊ†á */ icon: () => HTMLCanvasElement;
};

export namespace MaterialData {
    /** ÊùêÊñôÊï∞ÊçÆÁªÑ */
    type DataGroup = {
        tag?: MaterialTag;
        parent?: String;
        idPart?: String;
        list: Array<MaterialData>;
    };
    type ReactionData = {
        /** ÂèçÂ∫îÈÄüÂ∫¶ */
        speed: Number;
        /** Âø´ÈÄüÂèçÂ∫î */
        rapidly: Boolean;
        /** ÂéüÊñô */
        inputs: Array<DataGroup>;
        /** ‰∫ßÁâ© */
        outputs: Array<DataGroup>;
        /** ÊüêÁßç/Á±ªÊùêÊñô‰∏éËØ•ÂèçÂ∫î‰πãÈó¥ÁöÑËÅîÁ≥ª */
        getRelationship: (keyword: String | MaterialTag) => "asInput" | "asOutput" | "asCatalyzer" | "none";

        toString: () => String;
    };
}

export type Class = {
    new (): HTMLElement & {};
    readonly datas: Array<MaterialData>;
    readonly reactionDatas: Array<MaterialData.ReactionData>;
    queryById: (id: String) => MaterialData;
    queryByTag: (tag: MaterialTag) => Array<MaterialData>;
    queryReaction: (keyword: String | MaterialTag) => {
        asInput: Array<MaterialData.ReactionData>;
        asOutput: Array<MaterialData.ReactionData>;
        asCatalyzer: Array<MaterialData.ReactionData>;
    };
};
